<script src="assets/js/home.js"></script>
    <script>
        $("form").submit(function(e){
            e.preventDefault();
            let $this = $(this);
            $(this).find('.loader').addClass('loading');
            $(this).find('.common-button').addClass('loading');

            $.ajax({
                type: "POST",
                url: $(this).attr('action'),
                data: new FormData(this),
                dataType: 'json',
                contentType: false,
                cache: false,
                processData:false,
                success: function (data) {
                    if(data.status == 'success'){
                        swal.fire("Obrigado por deixar seu contato!", "Em breve retornaremos!", "success");

                        $this.find('.common-button').removeClass('loading');
                        $this.find('.loader').removeClass('loading');
                    }else{
                        swal.fire("Ocorreu um erro", data.message, "error");

                        $this.find('.common-button').removeClass('loading');
                        $this.find('.loader').removeClass('loading');
                    }
                },
                error: function (request, status, erro) {
                    console.log("Problema ocorrido: " + status + "\nDescrição: " + erro);
                    console.log("Informações da requisição: \n" + request.getAllResponseHeaders());

                    swal.fire("Não foi possível enviar seu contato!", "Tente novamente mais tarde!", "error");

                    $this.find('.common-button').removeClass('loading');
                    $this.find('.loader').removeClass('loading');
                }
            });
        });
    </script>
</body>
</html>